class App extends React.Component {
  state = {};
  constructor() {
    let today = new Date();
    state = {
      year: this.today.getFullYear(),
      month: this.today.getMonth() + 1,
      days: 0,
      minutes: this.today.getMinutes(),
      hours: this.today.getHours(),
      secounds: this.today.getSeconds()
    };
  }
  componentDidMount() {
    this.state.secounds = this.today.getSeconds();
  }
  render() {
    return (
      <div className={styles.screen}>
        <h1 className={styles.text}>{this.state.secounds}</h1>
        {/* <h1 className={styles.text}>{dateState}</h1> */}
      </div>
    );
  }
}

export default App;
