import React, { useState } from "react";
import styles from "./App.module.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date()
    };
  }
  componentDidMount() {
    setInterval(() => this.tick(), 1000);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }
  render() {
    const months = {
      0: "January",
      1: "February",
      2: "March",
      3: "April",
      4: "May",
      5: "June",
      6: "July",
      7: "August",
      8: "September",
      9: "October",
      10: "November",
      11: "December"
    };
    return (
      <div className={styles.screen}>
        {/* <h1 className={styles.text}>{this.state.date.toLocaleTimeString()}</h1> */}
        {/* <h1 className={styles.text}>
          {this.state.date.getMonth()}/{this.state.date.getDate()}/
          {this.state.date.getFullYear()}
        </h1> */}
        <h1 className={`${styles.text} ${styles.view3}`}>
          {months[this.state.date.getMonth()]}
          <br />
          {this.state.date.getDate() + "th"}{" "}
          {this.state.date
            .getFullYear()
            .toString()
            .slice(2, 4)}
        </h1>
      </div>
    );
  }
}

export default App;
