import React from "react";
import styles from "./Clock.module.scss";

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date()
    };
  }
  componentDidMount() {
    setInterval(() => this.tick(), 1000);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }
  render() {
    const months = {
      0: "January",
      1: "February",
      2: "March",
      3: "April",
      4: "May",
      5: "June",
      6: "July",
      7: "August",
      8: "September",
      9: "October",
      10: "November",
      11: "December"
    };
    return (
      <div>
        <h1
          className={`${styles.text} ${
            this.props.view === "1" ? styles.visible : ""
          }`}
        >
          {this.state.date.toLocaleTimeString("en-US")}
        </h1>

        <h1 className={styles.text}>
          {this.state.date.toLocaleDateString("en-US")}
        </h1>

        <h1 className={`${styles.text} ${styles.view3}`}>
          {months[this.state.date.getMonth()]}
          <br />
          {this.state.date.getDate() + "th "}
          {this.state.date
            .getFullYear()
            .toString()
            .slice(2, 4)}
        </h1>
      </div>
    );
  }
}

export default Clock;
